import { Component } from '@angular/core';
import { IonicPage, NavController, NavParams } from 'ionic-angular';
import { AngularFireAuth } from '@angular/fire/auth';
import { FirebaseServiceProvider } from '../../providers/firebase-service';
import { EventItemDetailsComponent } from '../../components/event-item-details/event-item-details';
import { EventItemAddComponent } from '../../components/event-item-add/event-item-add';


@IonicPage()
@Component({
  selector: 'page-events',
  templateUrl: 'events.html',
})
export class EventsPage {
  events: Array<any>;
  user: firebase.User;

  constructor(private afAuth: AngularFireAuth, public navCtrl: NavController, public navParams: NavParams,
    public firebaseService: FirebaseServiceProvider) {
    this.afAuth.authState.subscribe(user => this.user = user);
    this.getEvents();
  }

  openEvent(event) {
    this.navCtrl.push(EventItemDetailsComponent, { event: event });
    return;
  }

  getEvents() {
    // this.firebaseService.get('events', (events) =>
    //   this.events = events);
    this.events = [{
      title: 'Jane\'s Birthday Party',
      image: 'https://ak2.picdn.net/shutterstock/videos/12961142/thumb/1.jpg'
    }, {
      title: 'Summer Events',
      image: 'data:image/jpeg;base64,'
    }, {
      title: 'KFC Community Event',
      image: 'https://www.cbc.ca/parents//content/static_imgs/events_banner.jpg'
    }, {
      title: 'Hatfield Kids Marathon',
      image: 'https://bloximages.newyork1.vip.townnews.com/richmond.com/content/tncms/assets/v3/editorial/d/f2/df264856-a45a-11e2-8e5a-001a4bcf6878/51698de13ac65.image.jpg'
    }, {
      image: 'data:image/jpeg;base64,',
      title: 'PBS KIDS Events'
    },];
  }
  
  addNew() {
    this.navCtrl.push(EventItemAddComponent);
    return;
  }
}